var header=document.querySelector(".header"),aside=document.querySelector(".main__aside"),stickAside=function(e){if(window.pageYOffset>header.offsetHeight){var t=e.getBoundingClientRect();e.setAttribute("style","position: fixed; top: "+header.offsetHeight+"px; left: "+t.left+"px;")}else e.removeAttribute("style")};window.addEventListener("scroll",function(){stickAside(aside)});